        public string currentId;
        
        // ----------------------------------------------------------------------------------------
        public void Init()
        {
            _aSizes = new List<StructSize> {
                new StructSize {Size = 0.2f, Id = "20cm"},
                new StructSize {Size = 0.4f, Id = "40cm"},
                new StructSize {Size = 0.8f, Id = "80cm"}
            };

            SetSize(1);
            currentId = "";
            
            _shapePrefabs = new List<Dictionary<string, GameObject>>();
            foreach (var unused in _aSizes) _shapePrefabs.Add(new Dictionary<string, GameObject>());
            
            //var url = Path.Combine(Application.streamingAssetsPath, "Dragginz");
            //url = Path.Combine(url, "shape-data.json");
            //var jsonData = File.ReadAllText(url);
            
            // WebGL hack!
            var jsonData = GetJsonString();
            
            _blockShapes = new BlockShapes();
            _blockShapes.ParseJson(jsonData);

            // LogShapeData();
            /*foreach (var t1 in _blockShapes.shapeSets) {
                foreach (var t2 in t1.shapeTypes) {
                    foreach (var t3 in t2.shapeObjects) {
                        CreateShapePrefab(t3);
                    }
                }
            }*/
            
            shapePool.gameObject.SetActive(false);
        }

        // ----------------------------------------------------------------------------------------
        public void SetSize(int index)
        {
            _curSizeIndex = index;
            currentSize = _aSizes[_curSizeIndex].Size;
        }
        
        // ----------------------------------------------------------------------------------------
        public GameObject GetBlockGameObject(int iSet, int iType, int iShape, int iSize = -1)
        {
            currentId = _blockShapes.shapeSets[iSet].shapeTypes[iType].shapeObjects[iShape].id;

            var sizeIndex = iSize == -1 ? _curSizeIndex : iSize;
            var prefabs = _shapePrefabs[sizeIndex];
            if (!prefabs.ContainsKey(currentId))
            {
                CreateShapePrefab(_blockShapes.shapeSets[iSet].shapeTypes[iType].shapeObjects[iShape]);
            }
            
            return Instantiate(_shapePrefabs[sizeIndex][currentId]);
        }
        
        // ----------------------------------------------------------------------------------------
        public void PopulateSetsDropdown(Dropdown dd)
        {
            dd.ClearOptions();

            foreach (var s in _blockShapes.shapeSets)
            {
                dd.options.Add(new Dropdown.OptionData {text = s.id});
            }

            dd.captionText.text = dd.options[0].text;
            dd.value = 0;
        }

        // ----------------------------------------------------------------------------------------
        public void PopulateTypesDropdown(Dropdown dd, int setIndex)
        {
            dd.ClearOptions();

            var set = _blockShapes.shapeSets[setIndex];
            foreach (var t in set.shapeTypes)
            {
                dd.options.Add(new Dropdown.OptionData {text = t.id});
            }

            dd.captionText.text = dd.options[0].text;
            dd.value = 0;
        }
        
        // ----------------------------------------------------------------------------------------
        public void PopulateShapesDropdown(Dropdown dd, int setIndex, int typeIndex)
        {
            dd.ClearOptions();

            var t = _blockShapes.shapeSets[setIndex].shapeTypes[typeIndex];
            foreach (var s in t.shapeObjects)
            {
                dd.options.Add(new Dropdown.OptionData {text = s.id});
            }

            dd.captionText.text = dd.options[0].text;
            dd.value = 0;
        }
        
        // ----------------------------------------------------------------------------------------
        public void PopulateSizesDropdown(Dropdown dd)
        {
            dd.ClearOptions();

            foreach (var s in _aSizes) {
                dd.options.Add(new Dropdown.OptionData {text = s.Id});
            }

            dd.captionText.text = dd.options[1].text;
            dd.value = 1;
        }
        
        // ----------------------------------------------------------------------------------------
        //
        // ----------------------------------------------------------------------------------------
        private void CreateShapePrefab(ShapeObject obj)
        {
            var go = new GameObject(obj.id);
            go.transform.SetParent(shapePool);

            var meshRenderer = go.AddComponent<MeshRenderer>();
            var meshFilter = go.AddComponent<MeshFilter>();
            
            var mesh = new Mesh();
            mesh.SetVertices( CreateVector3List(obj.vertices, currentSize) );
            mesh.SetTriangles( CreateIntArray(obj.triangles), 0 );
            mesh.SetUVs(0, CreateVector3List(obj.uvs, currentSize,false) );
            mesh.SetNormals( CreateVector3List(obj.normals,1f) );
            mesh.RecalculateNormals();
            mesh.RecalculateBounds();
            mesh.RecalculateTangents();
            meshFilter.mesh = mesh;

            meshRenderer.sharedMaterial = prefabMaterial;
            
            go.AddComponent<BoxCollider>();
            go.tag = "Block";
            
            _shapePrefabs[_curSizeIndex].Add(obj.id, go);
        }

        // ----------------------------------------------------------------------------------------
        private static List<Vector3> CreateVector3List(string s, float size, bool hasZ = true)
        {
            var v3 = new List<Vector3>();

            var sizeHalf = size * 0.5f;
            
            var a = s.Split(';');
            var len = a.Length;
            for (var i = 0; i < len; ++i)
            {
                var b = a[i].Split(',');
                var v = new Vector3 {x = float.Parse(b[0]) * size - sizeHalf, y = float.Parse(b[1]) * size - sizeHalf, z = 0};
                if (hasZ) v.z = float.Parse(b[2]) * size + sizeHalf;

                v3.Add(v);
            }

            return v3;
        }

        // ----------------------------------------------------------------------------------------
        private static int[] CreateIntArray(string s)
        {
            var strArr = s.Split(',');
            var intArr = new int[strArr.Length];
            //Debug.Log(strArr.Length);
            var len = strArr.Length;
            for (var i = 0; i < len; ++i) {
                intArr[i] = int.Parse(strArr[i]);
            }

            return intArr;
        }

